# PROGRAMA 1# Escriba un programa que resuelva el siguiente problema: # la función coseno, puede evaluarse por medio de una serie infinita.#Pedir al usuario el valor de x x = input ("Coseno de Xº = ");#Pedir los terminos de la serie que serán utilizados para calcular el cosenoterminos = input ("Número de términos: ");#Condicion de que los terminos deben ser mayor a 0if terminos>=0    #Convertimos los grados dados por el usuario a radianes    xrad = ( x * pi ) / 180;    #Variable donde se guardará la sumatoria de la serie    cosx = 0;    #Variable para cambiar el signo     signo = 1;    #Variable para incrementar de 2 en 2    increm = 0;    #Se calcula el coseno en el ciclo for    for i = 1:terminos             cosx += ( ( ( xrad ^ increm ) / factorial ( increm ) ) * signo );             signo *= -1;       increm += 2;           endfor    #Imprimimos el resultado    fprintf ( "\n\nEl coseno de %gº es %g\n\n", x, cosx );else    #Se imprime el error    fprintf("ERROR: El número de términos que diste no es positivo.\n");end